import React from 'react';
import {Link} from "react-router-dom";
import avatar from '../assets/profile.png';
import styles from '../styles/Username.module.css';

const UserName = () => {
    return (
        <div className="container mx-auto">
            <div className="flex justify-center items-center h-screen">
                <div className={styles.glass}>

                    <div className="title flex flex-col items-center">
                        <h4 className="text-5xl font-bold"> Hello Again!</h4>
                        <span className="py-4 text-xl w-2/3 text-center text-gray-500">
                        Explore More by connecting with us.
                    </span>
                    </div>

                    <form className="py-1">
                        <div className="profile flex justify-center py-4">
                            <img src={avatar} className={styles.profile_img} alt="profile-photo"/>
                        </div>
                        <div className="textbox flrx flex-col items-center gap-6">
                            <input type="text" className={styles.textbox} placeholder="Username"/>
                            <button className={styles.btn} type="submit"> Let's Go </button>
                        </div>

                        <div className="text-center py-4">
                            <span className="text-gray-500">
                                Not a Member ?
                                <Link to="/register" className="text-red-500"> Register Now </Link>
                            </span>
                        </div>
                    </form>

                </div>
            </div>
        </div>
    );
};

export default UserName;
