import React, {useState} from 'react';
import {Link} from "react-router-dom";
import avatar from '../assets/profile.png';
import styles from '../styles/Username.module.css';
import { useFormik } from "formik";
import { registerValidation } from "../helper/validate";
import { Toaster } from "react-hot-toast";
import convertToBase64 from "../helper/conver";

const Profile = () => {

    const [file, setFile] = useState();

    const formik = useFormik({
        initialValues: {
            email: '',
            username: '',
            password: ''
        },
        validate: registerValidation,
        validateOnBlur: false,
        validateOnChange: false,
        onSubmit: async values => {
            values = await Object.assign(values, { profile: file || ''})
            console.log(values);
        }
    });

    const onUpload = async e => {
        const base64 = await convertToBase64(e.target.files[0]);
        setFile(base64);
    }

    return (
        <div className="container mx-auto">

            <Toaster position='top-center' reverseOrder={false}></Toaster>

            <div className="flex justify-center items-center h-screen">
                <div className={styles.glass} style={{width: "45%"}} >

                    <div className="title flex flex-col items-center">
                        <h4 className="text-5xl font-bold"> Profile </h4>
                        <span className="py-4 text-xl w-2/3 text-center text-gray-500">
                            You can update the details.
                        </span>
                    </div>

                    <form className="py-1" onSubmit={formik.handleSubmit}>
                        <div className="profile flex justify-center py-4">
                            <label htmlFor="profile">
                                <img src={file || avatar} className={styles.profile_img} alt="profile-photo"/>
                            </label>

                            <input onChange={onUpload} type="file" id="profile" name="profile"/>
                        </div>
                        <div className="textbox flex flex-col items-center gap-6">
                            <div className="name flex w-3/4 gap-10">
                                <input {...formik.getFieldProps('firstName')} type="text" className={styles.textbox} placeholder="FirstName"/>
                                <input {...formik.getFieldProps('lastName')} type="text" className={styles.textbox} placeholder="LastName"/>
                            </div>

                            <div className="name flex w-3/4 gap-10">
                                <input {...formik.getFieldProps('mobile')} type="text" className={styles.textbox} placeholder="Mobile No."/>
                                <input {...formik.getFieldProps('email')} type="text" className={styles.textbox} placeholder="Email"/>
                            </div>

                            <div className="name flex w-3/4 gap-10">
                                <input {...formik.getFieldProps('address')} type="text" className={styles.textbox} placeholder="Mobile No."/>
                                <input {...formik.getFieldProps('email')} type="text" className={styles.textbox} placeholder="Email"/>
                            </div>

                            <input {...formik.getFieldProps('username')} type="text" className={styles.textbox} placeholder="Username"/>
                            <input {...formik.getFieldProps('password')} type="password" className={styles.textbox} placeholder="Password"/>
                            <button className={styles.btn} type="submit"> Register </button>
                        </div>

                        <div className="text-center py-4">
                            <span className="text-gray-500">
                                Already Register ?
                                <Link to="/" className="text-red-500"> Login Now </Link>
                            </span>
                        </div>
                    </form>

                </div>
            </div>
        </div>
    );
};

export default Profile;
