import React from 'react';
import {Link} from "react-router-dom";
import avatar from '../assets/profile.png';
import styles from '../styles/Username.module.css';
import { useFormik } from "formik";
import { passwordValidate } from "../helper/validate";
import { Toaster } from "react-hot-toast";

const Pecovery = () => {

    const formik = useFormik({
        initialValues: {
            password: ''
        },
        validate: passwordValidate,
        validateOnBlur: false,
        validateOnChange: false,
        onSubmit: async values => {
            console.log(values);
        }
    });
    return (
        <div className="container mx-auto">

            <Toaster position='top-center' reverseOrder={false}></Toaster>

            <div className="flex justify-center items-center h-screen">
                <div className={styles.glass}>

                    <div className="title flex flex-col items-center">
                        <h4 className="text-5xl font-bold"> Recovery </h4>
                        <span className="py-4 text-xl w-2/3 text-center text-gray-500">
                            Enter OTP to recovery password.
                        </span>
                    </div>

                    <form className="pt-20" onSubmit={formik.handleSubmit}>
                        <div className="profile flex justify-center py-4">
                            <img src={avatar} className={styles.profile_img} alt="profile-photo"/>
                        </div>
                        <div className="textbox flex flex-col items-center gap-6">
                            <input {...formik.getFieldProps('password')} type="password" className={styles.textbox} placeholder="Password"/>
                            <button className={styles.btn} type="submit"> Sign In </button>
                        </div>

                        <div className="text-center py-4">
                            <span className="text-gray-500">
                                Forgot Password ?
                                <Link to="/recovery" className="text-red-500"> Recovery Now </Link>
                            </span>
                        </div>
                    </form>

                </div>
            </div>
        </div>
    );
};

export default Pecovery;
